<h1><?php echo _TR_listInstanceWithTheme ?> "<i><?php echo $categories[0]->name ?></i>"</h1><hr/><div class="row">	<div class="col-md-9">		<div class="listview">			<?php					if(sizeof($instances) > 0)				foreach($instances as $instance)				{					?>					<a href="<?php echo WEBSITE_ROOT ."/".$categories[0]->id."-". url($categories[0]->name)?>/instances/<?php echo $instance->id ."-".url($instance->name) ?>" class="list">						<div class="list-content">							<?php							if($instance->image != "")								echo "<img src='".WEBSITE_ROOT."/images/".$instance->image."' class='icon img-circle'>";							else								echo "<span class='glyphicon glyphicon-play-circle icon'></span>";							?>							<div class="data">								<span class="list-title fg-blue"><?php echo ucfirst($instance->name) ?></span>								<span class="list-remark">									<?php echo ucfirst(strip_tags($instance->descr)) ?>									<br /><br />									<i><?php echo _TR_Deadline ?> : <?php echo $instance->deadline ?></i>								</span>															</div>						</div>					</a>					<?php				}				else				{					echo _TR_NoData;				}			?>		</div>	</div>		<div class="col-md-2 col-md-offset-1">		<div class="alert alert-info">			<strong><?php echo _TR_Help ?></strong> <?php echo _TR_InstancesListHelp."<br /><br /> ". _TR_InstancesHelp ?>		</div>		<?php		if(isset($_SESSION['users']))		{			?>			<h3><?php echo _TR_Actions ?></h3>			<a class="btn btn-primary"  data-toggle="modal" data-target="#myModal"  href="<?php echo WEBSITE_ROOT ."/". $categories[0]->id ."-". url($categories[0]->name)?>/instances/add"><?php echo _TR_AddAnInstance ?></a>			<?php		}		?>	</div></div>