<h1><?php echo _TR_listInstanceWithTheme ?> <i class="fg-blue">"<?php echo $categories->name ?>"</i></h1><hr/><div class="row">	<div class="col-md-9">		<div class="listview">			<?php					if(sizeof($instances) > 0)				foreach($instances as $instance)				{					?>					<a href="<?php echo WEBSITE_ROOT ."/".$categories->id."-". url($categories->name) ?>/instances/<?php echo $instance->id ."-".url($instance->name) ?>" class="list">						<div class="list-content">							<?php							if($instance->image != "")								echo "<img src='".WEBSITE_ROOT."/images/".$instance->image."' class='icon img-circle'>";							else								echo "<span class='glyphicon glyphicon-play-circle icon'></span>";							?>							<div class="data">								<span class="list-title fg-blue"><?php echo ucfirst($instance->name) ?></span>								<span class="list-remark">									<?php echo ucfirst(strip_tags($instance->descr)) ?>									<br /><br />									<i><?php echo _TR_Deadline ?> : <?php echo $instance->deadline ?></i>								</span>															</div>						</div>					</a>					<?php				}				else				{					echo _TR_NoData;				}			?>		</div>	</div>		<div class="col-md-3">		<div class="alert alert-warning">			<b><?php echo _TR_Help ;?></b>  <?php echo _TR_InstancesListHelp."<br /><br /> ". _TR_InstancesHelp ?>		</div>				<br />		<?php 		if(isset($_SESSION['delegations']['receiveForCategories']) && !empty($_SESSION['delegations']['receiveForCategories']))		{			echo '<div class="alert bg-lightMauve fg-gray ">				<span class="glyphicon glyphicon-ok"></span>&nbsp;';			echo _TR_DelegationReceiveFrom ;			foreach($_SESSION['delegations']['receiveForCategories'] as $value)				echo $value->users1Name.", ";			echo '</div>';		}		?>		<?php		/****************************		********* BLOC ACTION *******		*****************************/		if(isset($_SESSION['users']))		{			?>			<br /><br />			<h3><?php echo _TR_Actions ?></h3>			<a class="btn btn-primary"  data-toggle="modal" data-target="#myModal"  id="addInstanceButton" href="<?php echo WEBSITE_ROOT ."/". $categories->id ."-". url($categories->name)?>/instances/add">				<span class='glyphicon glyphicon glyphicon-plus'></span>				<?php echo _TR_AddAnInstance ?>			</a>			<br />			<br />			<?php			//Delete delegation button			if(isset($_SESSION['delegations']['categories']))			{				?>				<div class="btn-group">					<span class="btn btn-mauve cursor-pointer">				  		<span class='glyphicon glyphicon glyphicon-user'></span>				  		<?php echo _TR_DelegationGivenTo."<br />".$_SESSION['delegations']['categories']->users2Name; ?>					</span>					<button type="button" class="btn btn-mauve dropdown-toggle" data-toggle="dropdown">				    	<span class="caret"></span>				    	<span class="sr-only">Toggle Dropdown</span>				    </button>					  <ul class="dropdown-menu" role="menu">					    	<li>					    		<a data-toggle="modal" data-target="#myModal"  id="addInstanceButton" href="<?php echo WEBSITE_ROOT ."/". $categories->id ."-". url($categories->name)?>/instances/deleteDelegation">					    			<span class='glyphicon glyphicon-remove'></span> 					    			<?php echo _TR_DeleteDelegation; ?>					    		</a>					    	</li>					  </ul>				 </div>				<?php									}				//add delegation button				else				{					?>					<a class="btn btn-mauve"  data-toggle="modal" data-target="#myModal"  id="addInstanceButton" href="<?php echo WEBSITE_ROOT ."/". $categories->id ."-". url($categories->name)?>/instances/addDelegation">						<span class='glyphicon glyphicon glyphicon-user'></span>						<?php echo _TR_AddDelegation ?>					</a>					<?php 				}		}		?>	</div></div>