<?phpnamespace library\webComponents;/*** bool web component* @author cyril bazin* @package cu.core* @version 1.0*/class bool extends \library\field{	private $selected0, $selected1 = ''; 		public function build()	{		switch((int)$this->value)		{			case 0:				$this->selected0 = "selected=selected";				break;			case 1:				$this->selected1 = "selected=selected";				break;		}			$widget = '';				$widget .= '						<select name="'.$this->name.'">					<option value="0"' .$this->selected0.'>'.convertCodes(0).'</option>					<option value="1"' .$this->selected1.'>'.convertCodes(1).'</option>			';				//if (!empty($this->value))		//	$widget .= ''.htmlspecialchars($this->value).'';				return $widget .= ' </select></td></tr>';	}}?>