<?phpnamespace library\webComponents;/*** checkbox web component* @author cyril bazin* @package cu.core* @version 1.0*/class checkbox extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="checkbox" name="'.$this->name.'"';				if (!empty($this->value))			$widget .= ' checked="'.htmlspecialchars($this->value).'"';				if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';				$widget .= ' />';				if(!empty($this->data))			$widget .= '			<div class="alert" style="float:right;width:60%;">				<button type="button" class="close" data-dismiss="alert">&times;</button>'.$this->data.'			</div>';						return $widget;	}}?>