<?phpnamespace library\webComponents;/*** date picker web component* @author cyril bazin* @package cu.core*/class datePicker extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="text" name="'.$this->name.'"';				if (!empty($this->value))			$widget .= ' value="'.$this->value.'"';							if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';				$widget .= ' class="form-control datepicker';				if (!empty($this->class))			$widget .= ' '.$this->class;		$widget .= '"';					$widget .= " />";				return $widget;	}}?>