<?phpnamespace library\webComponents;/*** email web component* @author cyril bazin* @package cu.core*/class email extends \library\field{	public function build()	{		$widget = '';				$widget .= '<div class="input-group"><span class="input-group-addon"><span class="glyphicon glyphicon-envelope"></span></span>';				$widget .= '<input class="form-control" placeholder="Adresse email" type="text" name="'.$this->name.'"';				if(!empty($this->required))			$widget .= 'required ';				if (!empty($this->value))			$widget .= ' value="'.htmlspecialchars($this->value).'"';				if (!empty($this->maxLength))			$widget .= ' maxlength="'.$this->maxLength.'"';					if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';					$widget .= " /></div>";				return $widget;	}}?>