<?phpnamespace library\webComponents;/*** hidden web component* @author cyril bazin* @package cu.core* @version 1.0*/class hidden extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="hidden" name="'.$this->name.'"';				if (!empty($this->value))			$widget .= ' value="'.htmlspecialchars($this->value).'"';							$widget .= " />";				return $widget;	}}?>