<?phpnamespace library\webComponents;/*** image web component* @author cyril bazin* @package cu.core* @version 1.0*/class image extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="file" name="'.$this->name.'"';				if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';					if(!empty($this->value))			$widget .= ' value="'.$this->value.'"';				$widget .= " />";				if(!empty($this->value))			$widget .= '				<br />				<br />				<input type="text" name="image" id="image" value="'.$this->value.'" readonly=readonly/>				<a href="'.WEBSITE_ROOT.'/images/'.$this->value.'" target="_blank"> <i class="icon-fullscreen" title="'._TR_Fullscreen.'"></i></a>				<a href="#" onclick="$(\'#image\').val(\'\');"> <i class="icon-remove" title="'._TR_Delete.'"></i></a>				<br /><br />				<a data-toggle="modal" data-target="#myModal" href="'. WEBSITE_ROOT."/img/".$this->value.'"><img style="max-width:250px;" src="'.WEBSITE_ROOT.'/images/'.$this->value.'" alt="" title="" class="img-thumbnail "/></a>';										return $widget;				}}?>