<?phpnamespace library\webComponents;/*** image web component* @author cyril bazin* @package cu.core*/class image extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="file" name="'.$this->name.'"';				if(!empty($this->readonly))			$widget .= ' disabled="disabled"';					if(!empty($this->value))			$widget .= ' value="'.$this->value.'"';				$widget .= " />";				if(!empty($this->value))			$widget .= '				<br />				<br />				<input type="text" name="image" id="image" value="'.$this->value.'" readonly=readonly/>				<br /><br />				<a data-toggle="modal" data-target="#myModal" href="'. WEBSITE_ROOT."/img/".$this->value.'">					<img style="max-width:250px;" src="'.WEBSITE_ROOT.'/images/'.$this->value.'" alt="" title="" class="img-thumbnail "/>				</a>';										return $widget;				}}?>