<?phpnamespace library\webComponents;/*** password web component* @author cyril bazin* @package cu.core* @version 1.0*/class password extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="password" name="'.$this->name.'"';				if (!empty($this->value))			$widget .= ' value="'.htmlspecialchars($this->value).'"';				return $widget .= ' />';	}}?>