<?phpnamespace library\webComponents;/*** price web component* @author cyril bazin* @package cu.core* @version 1.0*/class price extends \library\field{	public function build()	{		$widget = '';				$widget .= '<input type="text" name="'.$this->name.'"';				if (!empty($this->value))			$widget .= ' value="'.htmlspecialchars($this->value).'"';				if (!empty($this->maxLength))			$widget .= ' maxlength="'.$this->maxLength.'"';					if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';				return $widget .= ' />';	}}?>