<?phpnamespace library\webComponents;/*** select web component* @author cyril bazin* @package cu.core*/class select extends \library\field{	protected $value = null;	protected $values = array();		public function build()	{				$widget = '';				$widget .= '<select class="form-control" name="'.$this->name.'">';		$widget .= "<option value=''></option>";				foreach($this->values as $key=>$val)		{			$selected = ($val->id == $this->value) ? "selected='selected'" : "";				$widget .= '<option value="'.$val->id.'" '.$selected.'>'.$val->name.'</option>';				 		}				return $widget .= ' </select>';	}}?>