<?phpnamespace library\webComponents;/*** textarea web component* @author cyril bazin* @package cu.core*/class textarea extends \library\field{	public function build()	{		$widget = '';				$widget .= '<textarea name="'.$this->name.'"';						if(!empty($this->class))			$widget .= ' class="form-control '.$this->class.'"';		else		$widget .= ' class="form-control" ';					if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';					if(!empty($this->rows))			$widget .= ' rows="'.$this->rows.'"';							$widget .= '>';				if (!empty($this->value))			$widget .= ''.htmlspecialchars($this->value).'';				$widget .= ' </textarea>';							return $widget;	}}?>