<?phpnamespace library\webComponents;/*** status web component* @author cyril bazin* @package cu.core* @version 1.0*/class typeOfWorkflowAnswer extends \library\field{	private $selected1000, $selected1001, $selected1002, $selected1003 = '', $selected1004 = '', $selected1005 = ''; 			/**	* build the type of answer	* 1000 => validate	* 1001 => simple select	* 1002 => multiple select	* 1003 => textarea	* 1004 => import doc	* 1005 => valid doc	*/	public function build()	{		switch($this->value)		{			case 1000:				$this->selected1000 = "selected=selected";				break;			case 1001:				$this->selected1001 = "selected=selected";				break;			case 1002:				$this->selected1002 = "selected=selected";				break;			case 1003:				$this->selected1003 = "selected=selected";				break;			case 1004:				$this->selected1004 = "selected=selected";				break;			case 1005:				$this->selected1005 = "selected=selected";				break;		}			$widget = '';				$widget .= '				<select name="'.$this->name.'">					<option value="1000" '.$this->selected1000.'>'.convertCodes(1000).'</option>					<option value="1003" '.$this->selected1003.'>'.convertCodes(1003).'</option>					<option value="""> ----- NON IMPLEMENTE ----- </option>					<option value="1001" '.$this->selected1001.'>'.convertCodes(1001).'</option>					<option value="1002" '.$this->selected1002.'>'.convertCodes(1002).'</option>					<option value="1004" '.$this->selected1004.'>'.convertCodes(1004).'</option>					<option value="1005" '.$this->selected1005.'>'.convertCodes(1005).'</option>			';				if (!empty($this->value))			$widget .= ''.htmlspecialchars($this->value).'';						$widget .= " </select>";				return $widget;	}}?>